declare module '*module.scss' {
    const classes: {readonly [key: string]: string};
    export default classes;
}

declare module "*.png" {
    const content: any;
    export default content;
}

declare module "*.svg" {
    const content: any;
    export default content;
}

declare module "*.mp4" {
    const content: any;
    export default content;
}


declare module "*.webm" {
    const content: any;
    export default content;
}